import React, { useState, useEffect } from "react";
import {
  ImageBackground,
  StyleSheet,
  Text,
  View,
  Image,
  TextInput,
  TouchableOpacity,
} from "react-native";
import Geolocation from "@react-native-community/geolocation";
import { useFonts } from "expo-font";

const API_KEY = "fde70b7b510d418c6126c7433ab077c4"; // API KEY FROM OPENWEATHERMAP

//FETCHING DATA FROM OPENWEATHERMAP API

const fetchWeatherData = async (latitude, longitude) => {
  const url = `https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=${API_KEY}&units=metric`;
  const response = await fetch(url);
  const data = await response.json();
  console.log(data);
  return data;
};
// FETCHING DATA ENDS

// Fetching Day, Month and Date from Date Object

export default function App() {
  const [fontsLoaded] = useFonts({
    "Mont": require("./Fonts/Montserrat-Medium.ttf"),
  });

  const onLayoutRootView = useCallback(async () => {
    if (fontsLoaded) {
      await SplashScreen.hideAsync();
    }
  }, [fontsLoaded]);

  if (!fontsLoaded) {
    return null;
  }

  const dateObj = new Date();
  const dayName = dateObj.toLocaleString("default", { weekday: "long" });
  const dayNum = dateObj.getDate();
  const month = dateObj.toLocaleString("default", { month: "long" });
  const date = `${dayName}, ${dayNum} ${month}`;

  // Fetching Day, Month and Date from Date Object ENDS

  // GETTING WEATHER ICONS

  const getWeatherIcon = (weatherCondition, hour) => {
    const weatherIcons = {
      Clear: [
        require("./assets/ClearDay.png"),
        require("./assets/ClearNight.png"),
      ],
      Clouds: require("./assets/Cloud.png"),
      Rain: require("./assets/Rain.png"),
      Wind: require("./assets/Wind.png"),
      Snow: require("./assets/Snow.png"),
      Fog: require("./assets/Fog.png"),
      Drizzle: require("./assets/Drizzle.png"),
      Tornado: require("./assets/Tornado.png"),
      Ash: require("./assets/DustSandAsh.png"),
      Dust: require("./assets/DustSandAsh.png"),
      Sand: require("./assets/DustSandAsh.png"),
      Haze: require("./assets/HazeMistSmoke.png"),
      Mist: require("./assets/HazeMistSmoke.png"),
      Smoke: require("./assets/HazeMistSmoke.png"),
      Squall: require("./assets/ThunderstormSquall.png"),
      Thunderstorm: require("./assets/ThunderstormSquall.png"),
    };

    if (Array.isArray(weatherIcons[weatherCondition])) {
      if (hour >= 18.5 || hour <= 6) return weatherIcons[weatherCondition][1];
      return weatherIcons[weatherCondition][0];
    }
    return weatherIcons[weatherCondition];
  };

  // GETTING WEATHER ICONS ENDS

  //Fetching location

  const [data, setData] = useState(null);
  const [weatherIcon, setWeatherIcon] = useState(null);

  useEffect(() => {
    Geolocation.getCurrentPosition((position) => {
      const { latitude, longitude } = position.coords;
      fetchWeatherData(latitude, longitude).then((data) => {
        setData(data);
      });
    });
  }, []);

  //Fetching location ENDS

  //Fetching Weather data

  useEffect(() => {
    if (data) {
      const hour = new Date().getHours();
      const weatherCondition = data.weather[0].main;
      const weatherIcon = getWeatherIcon(weatherCondition, hour);
      setWeatherIcon(weatherIcon);
    }
  }, [data]);

  //Fetching Weather data ENDS

  // RENDER STARTS

  return (
    <ImageBackground
      style={styles.backgroundImage}
      source={require("./assets/BG_Gradient.png")}
    >
      <View style={styles.searchBar}>
        <TextInput style={styles.input} placeholder="Search City" />
        <TouchableOpacity>
          <Image
            source={require("./assets/search-loc.png")}
            style={styles.searchIcon}
          />
        </TouchableOpacity>
      </View>
      <View style={styles.Datecontainer}>
        <Text style={styles.DateText}>{date}</Text>
      </View>
      <View>
        {data ? (
          <View style={styles.CurrentLocation}>
            <Text style={styles.CurrentLocationText}>
              {data.name}, {data.sys.country}
            </Text>
          </View>
        ) : (
          <Text style={styles.CurrentLocationText}></Text>
        )}
      </View>
      <View style={styles.weatherImage}>
        <View>
          <Image source={weatherIcon} style={{ width: 120, height: 120 }} />
        </View>
        <Text style={styles.weatherImageText}>
          {data ? data.weather[0].description : ""}
        </Text>
        <Text style={styles.cloudCover}>
          {data ? `Clouds Cover: ${data.clouds.all}%` : ""}
        </Text>
      </View>
      <View style={styles.temp}>
        <Text style={styles.tempText}>
          {data ? `${data.main.temp.toFixed(1)}°C` : "_ _"}
        </Text>
      </View>

      <View style={styles.grid}>
        {/*Feels Like */}

        <View style={styles.box}>
          <View style={styles.boxHeader}>
            <Image
              style={styles.boxHeaderIcon}
              source={require("./assets/FeelsLike.png")}
            />
            <Text style={styles.boxHeaderText}>Feels Like</Text>
          </View>
          <Text style={styles.BoxMainText}>
            {data ? `${data.main.feels_like.toFixed(0)}°C` : ""}
          </Text>
        </View>
        {/* Wind */}

        <View style={styles.box}>
          <View style={styles.boxHeader}>
            <Image
              style={styles.boxHeaderIcon}
              source={require("./assets/Wind.png")}
            />
            <Text style={styles.boxHeaderText}>Wind</Text>
          </View>
          <Text style={styles.BoxMainText}>
            {data ? `${data.wind.speed.toFixed(1)} m/s` : ""}
          </Text>
        </View>

        {/* Humidity */}

        <View style={styles.box}>
          <View style={styles.boxHeader}>
            <Image
              style={styles.boxHeaderIcon}
              source={require("./assets/Humidity.png")}
            />
            <Text style={styles.boxHeaderText}>Humidity</Text>
          </View>
          <Text style={styles.BoxMainText}>
            {data ? `${data.main.humidity}%` : ""}
          </Text>
        </View>

        {/* Visibility */}

        <View style={styles.box}>
          <View style={styles.boxHeader}>
            <Image
              style={styles.boxHeaderIcon}
              source={require("./assets/Visibility.png")}
            />
            <Text style={styles.boxHeaderText}>Visibility</Text>
          </View>
          <Text style={styles.BoxMainText}>
            {data ? `${data.visibility / 1000} km` : ""}
          </Text>
        </View>
      </View>
    </ImageBackground>
  );
}

// RENDER ENDS

// STYLESHEET STARTS

const styles = StyleSheet.create({
  backgroundImage: {
    flex: 1,
  },

  searchBar: {
    flexDirection: "row",
    alignItems: "center",
    backgroundColor: "rgba(241, 241, 241, 0.95)",
    borderRadius: "2rem",
    marginVertical: "2rem",
    marginHorizontal: "2rem",
    paddingHorizontal: "1rem",
  },

  input: {
    flex: 1,
    paddingVertical: "1rem",
    fontFamily: "Montserrat",
    fontSize: "1rem",
    outlineStyle: "none",
    outlineColor: "transparent",
  },

  searchIcon: {
    width: "1.5rem",
    height: "1.5rem",
  },

  Datecontainer: {
    alignItems: "center",
    justifyContent: "center",
  },

  DateText: {
    fontFamily: "Montserrat",
    fontSize: "1.5rem",
    color: "#f1f1f1",
    fontStyle: "italic",
    fontWeight: "bold",
    textAlign: "center",
  },

  CurrentLocation: {
    alignItems: "center",
    justifyContent: "center",
    paddingTop: "0.7rem",
  },

  CurrentLocationText: {
    fontFamily: "Montserrat",
    fontSize: "1.3rem",
    fontVariant: ["small-caps"],
    color: "#f1f1f1",
    fontWeight: "500",
    textAlign: "center",
  },

  weatherImage: {
    alignItems: "center",
    justifyContent: "center",
    paddingTop: "2rem",
  },

  weatherImageText: {
    fontSize: "1.7rem",
    fontFamily: "Montserrat",
    color: "#f1f1f1",
    textTransform: "capitalize",
    fontWeight: "500",
    textAlign: "center",
  },

  cloudCover: {
    fontSize: "1rem",
    fontFamily: "Montserrat",
    color: "#f1f1f1",
    textTransform: "capitalize",
    fontWeight: "500",
    textAlign: "center",
    marginTop: "0.5rem",
  },

  temp: {
    marginTop: "1rem",
  },

  tempText: {
    fontSize: "4rem",
    fontFamily: "Montserrat",
    color: "#ffffff",
    fontWeight: "normal",
    textAlign: "center",
  },

  feelLikeText: {
    fontSize: "1rem",
    fontFamily: "Montserrat",
    color: "#f1f1f1",
    fontWeight: "500",
    textAlign: "center",
  },

  grid: {
    flex: 1,
    flexDirection: "row",
    flexWrap: "wrap",
    alignItems: "center",
    justifyContent: "center",
    marginVertical: "2rem",
    gap: 30,
  },

  box: {
    width: "10rem",
    height: "10rem",
    borderRadius: 10,
    backgroundColor: "rgba(241, 241, 241, 0.15)",
  },

  boxHeader: {
    flexDirection: "row",
    gap: 10,
    opacity: 0.8,
  },

  boxHeaderIcon: {
    width: "1.75rem",
    height: "1.75rem",
    marginLeft: "0.5rem",
    marginTop: "0.8rem",
  },

  boxHeaderText: {
    fontSize: "1.1rem",
    fontFamily: "Montserrat",
    color: "#f1f1f1",
    fontWeight: "500",
    paddingVertical: "1rem",
  },

  BoxMainText: {
    fontSize: "2rem",
    fontFamily: "Montserrat",
    color: "#f1f1f1",
    fontWeight: "500",
    textAlign: "auto",
    paddingLeft: "0.5rem",
    paddingTop: "1rem",
  },
});

// STYLESHEET ENDS
